changeSet.sql:

INSERT INTO TB_FATURAMENTOCABECALHO
SELECT GEN_FATURAMENTOCABECALHO.NEXTVAL, CD_CAIXACABECALHO,CD_CLIENTE,DT_MOVIMENTO,VL_BRUTO+VL_ACRESCIMO-VL_DESCONTO FROM TB_FATURAMENTO;

INSERT INTO TB_FATURAMENTODETALHE
SELECT	GEN_FATURAMENTODETALHE.NEXTVAL,(SELECT C.ID_FATURAMENTOCABECALHO FROM TB_FATURAMENTOCABECALHO C WHERE C.CD_CAIXACABECALHO=FC.CD_CAIXACABECALHO AND
C.DT_MOVIMENTO=FC.DT_MOVIMENTO AND C.VL_BRUTO=FC.VL_BRUTO),CD_HISTORICO,CD_VEICULO,CD_MOTORISTA,NR_DOCUMENTO,VL_BRUTO,VL_DESCONTO,VL_ACRESCIMO FROM
TB_FATURAMENTO FC INNER JOIN TB_FATURAMENTOTRANSPORTE ON TB_FATURAMENTOTRANSPORTE.CD_FATURAMENTO = FC.ID_FATURAMENTO;

INSERT INTO	TB_FATURAMENTOPAGAMENTOS
SELECT GEN_FATURAMENTOPAGAMENTOS.NEXTVAL, (SELECT C.ID_FATURAMENTOCABECALHO FROM TB_FATURAMENTOCABECALHO C WHERE
C.CD_CAIXACABECALHO=FC.CD_CAIXACABECALHO AND C.DT_MOVIMENTO=FC.DT_MOVIMENTO AND C.VL_BRUTO=FC.VL_BRUTO), CD_FORMAPAGAMENTO, DT_LANCAMENTO,
DT_VENCIMENTO, NR_PARCELA, VL_PARCELA FROM	TB_FATURAMENTO FC	INNER JOIN TB_FATURAMENTOPAGAMENTO	ON TB_FATURAMENTOPAGAMENTO.CD_FATURAMENTO =	FC.ID_FATURAMENTO;


INSERT INTO TB_FATURAMENTOENTRADASCABECALHO
SELECT gen_faturamentoentradascabecalho.nextval,CD_CAIXACABECALHO,CD_CLIENTE,DT_MOVIMENTO,VL_BRUTO+VL_ACRESCIMO-VL_DESCONTO FROM TB_FATURAMENTOENTRADA;

INSERT INTO TB_FATURAMENTOENTRADASDETALHE
SELECT gen_FATURAMENTOENTRADASDETALHE.nextval,(SELECT ID_FATURAMENTOENTRADASCABECALHO FROM TB_FATURAMENTOENTRADASCABECALHO C WHERE C.CD_CAIXACABECALHO=FC.CD_CAIXACABECALHO AND C.CD_CLIENTE=FC.CD_CLIENTE AND C.DT_MOVIMENTO=FC.DT_MOVIMENTO AND C.VL_BRUTO=FC.VL_BRUTO),CD_HISTORICO,CD_VEICULO,CD_MOTORISTA,NR_DOCUMENTO,VL_BRUTO,VL_DESCONTO,VL_ACRESCIMO FROM TB_FATURAMENTOENTRADA FC;

INSERT INTO	TB_FATURAMENTOENTRADAPAGAMENTOS
SELECT GEN_FATURAMENTOPAGAMENTOS.NEXTVAL, (SELECT ID_FATURAMENTOENTRADASCABECALHO FROM TB_FATURAMENTOENTRADASCABECALHO C WHERE C.CD_CAIXACABECALHO=FC.CD_CAIXACABECALHO AND C.CD_CLIENTE=FC.CD_CLIENTE AND C.DT_MOVIMENTO=FC.DT_MOVIMENTO AND C.VL_BRUTO=FC.VL_BRUTO), CD_FORMAPAGAMENTO, DT_LANCAMENTO,
DT_VENCIMENTO, NR_PARCELA, VL_PARCELA FROM	TB_FATURAMENTOENTRADA FC INNER JOIN TB_FATURAMENTOENTRADAPAGAMENTO	ON TB_FATURAMENTOENTRADAPAGAMENTO.CD_FATURAMENTOENTRADA =	FC.ID_FATURAMENTOENTRADA;
